import React from 'react';

export default class DelayedButton extends React.Component{
    render(){
      return <button onClick={this.clickhander}
      >Delay</button>
    }

    clickhander = (event) => {
        event.persist()
        window.setTimeout(this.props.onDelayedClick(event), this.props.delay)
    }
}

